$(document).ready(function(){
  $("#uploadBtn").click(function() {
   		var picture = $("#picture").val().replace(/.+[\\\/]/,"");
      $.get('/upload',{pict: picture}, function(data) {
        $('#photo').html(data);
        //ScrollTop
      });
  });

  $("#min, #med, #max, #con").attr('disabled','disabled');
  
  $(".bin").children(".name").click(
    function() {
      $(".bin").children("img").animate({ opacity: "0" }, 500);
      $(".bin").children(".name, .bin_type").animate({ top: "-50px"},500);
      $(".bin_type").animate({opacity: "1"},500);
    });

  $(".noize").children(".name").click(
    function() {
      $(".noize").children("img").animate({ opacity: "0" }, 500);
      $(".noize").children(".name, .noize_type").animate({ top: "-50px"},500);
      $(".noize_type").animate({opacity: "1"},500);
    });

  $("#yani").click( //GREY
    function(){
      var picture = $("#picture").val();
      $.get('/editgrey',{pict: picture}, function(data) {
        $("#image").remove();
        $('#photo').html(data);
      });
      $("#yani, #pik").attr('disabled','disabled');
      $("#yani, #pik, .bin").css("background-color","#aeaeae");
      $("#min, #med, #max, #con").prop('disabled',false);
      $(".noize, .noize_type").css("background-color","#717ece");
      $(".stat").css("background-color","#10df3c");
    });

  $("#pik").click(  //PIK
    function(){
      var picture = $("#picture").val();
      $.get('/editpik',{pict: picture}, function(data) {
        $("#image").remove();
        $('#photo').html(data);
      });
      $("#yani, #pik").attr('disabled','disabled');
      $("#yani, #pik, .bin").css("background-color","#aeaeae");
      $("#min, #med, #max, #con").prop('disabled',false);
      $(".noize, .noize_type, .stat").css("background-color","#717ece");
      $(".stat").css("background-color","#10df3c");
    });


//****************************************************************************
    $("#max").click(
      function(){
        $.get('/getmax', function(data){
          $("#image").remove();
          $('#photo').html(data);
        });
      });

    $("#min").click(
      function(){
        $.get('/getmin', function(data){
          $("#image").remove();
          $('#photo').html(data);
        });
      });

    $("#med").click(
      function(){
        $.get('/getmed', function(data){
          $("#image").remove();
          $('#photo').html(data);
        });
      });

    $("#con").click(
      function(){
        $.get('/getcon', function(data){
          $("#image").remove();
          $('#photo').html(data);
        });
      });
    $("#reset").click(
      function(){
        $.get('/reset',function(data){
          $("#image").remove();
          $('#photo').html(data);
        });
      });
      $(".stat").click(
      function(){
        $.get('/stat',{   },function(data){
          $('#statistic').html(data);
        });
      });
});
//******************************************************************
